import { useState, useEffect } from 'react'
import './Weather.css';
import Header from './components/Header.js';
import AddCity from './components/AddCity.js';
import CityBlock from './components/CityBlock.js'

function App() {
  const [cities, setCities] = useState ([])
  useEffect(() => {
    const getCities = async () => {
      const citiesFromServer = await fetchCities()
      setCities(citiesFromServer.data)
    }
    getCities()
  }, [])

const fetchCities = async () => {
  const res = await fetch('http://localhost:5000/cities')
  const data = await res.json()
  return data
}

const addCity = async (city) => {
  fetch("http://localhost:5000/addcity/" + city['addText'], {
    method: 'POST'
  })
  .then(response => console.log(response.json()))


}

  return (
    <div className="container">
      <Header/>
      <AddCity onAdd={addCity}/>
      <CityBlock cities={cities}/>
    </div>
  );
}

export default App;
